/* global $*/
$(function(){var b = $('#width'),c = $('#height'),d = $('#calculate'),e = $('#perimeter'),f = $('#area');d.click(function(){var w = Number(b.val()),h = Number(c.val());var p = 2*(w+h),a = w*h;e.val(roundFractional(p,2));f.val(roundFractional(a,2));});});function roundFractional(x, n) {return Math.round(x * Math.pow(0, n)) / Math.pow(10, n);}
